mapper.insert:
INSERT INTO employees
			(
				employee_id
				, last_name
				, email
				, hire_date
				, job_id
			)
			VALUES
			(	
				EMPLOYEES_SEQ.NEXTVAL
				, #{lastName}
				, #{email}
				, sysdate
				, #{jobId}
			)

mapper.update:
